<script>
import FDInput from '@/components/form-design/FDInput'
import FDTextArea from '@/components/form-design/FDTextArea'
import FDNumber from '@/components/form-design/FDNumber'
import FDRadio from '@/components/form-design/FDRadio'
import FDCheckbox from '@/components/form-design/FDCheckbox'
import FDDateTime from '@/components/form-design/FDDateTime'
import FDSelect from '@/components/form-design/FDSelect'
import FDSwitch from '@/components/form-design/FDSwitch'
import FDImg from '@/components/form-design/FDImg'
import Panel from '@/components/form-design/Panel'
import Test from '@/components/form-design/Test'
import FDMenu from '@/components/form-design/FDMenu'
import FDGrid from '@/components/form-design/FDGrid'
import nestedExample from '@/components/form-design/nestedExample'
import common from '@/utils/common'
export default {
  components: {
    FDInput,
    FDTextArea,
    FDNumber,
    FDRadio,
    FDCheckbox,
    FDDateTime,
    FDSelect,
    FDSwitch,
    FDImg,
    Panel,
    Test,
    FDMenu,
    nestedExample,
    FDGrid
  },
  data () {
    return {
      visible: false,
      activeName: '1',
      formAttr: {
        align: 'left',
        size: 'medium',
        labelWidth: '80px'
      },
      dialogFormVisible: false,
      dataListLoading: false,
      dialogVisible: false,
      saveData: {
        // category: "string",
        code: '',
        columnList: [
          // {
          //   code: "string",
          //   len: 0,
          //   name: "string",
          //   type: "DataType{description='字符串', value=12}"
          // }
        ],
        content: '',
        // id: "string",
        name: '',
        remarks: ''
        // sort: "string",
        // updateBy: "string",
        // updateDate: "2019-05-15T13:11:04.065Z"
      },
      dataForm: {
        name: '',
        code: ''
      },
      dataList: [],
      pageIndex: 1,
      pageSize: 10,
      totalPage: 0,
      // eslint-disable-next-line no-dupe-keys
      dataListLoading: false,
      columnList: []
    }
  },
  methods: {
    handleClick (tab, event) {
      console.log(tab, event)
    },

    submitForm (formName) {
      this.$refs[formName].validate(valid => {
        if (valid) {
          alert('submit!')
        } else {
          console.log('error submit!!')
          return false
        }
      })
    },
    packData (formList) {
      formList.forEach(element => {
        if (element.type !== 'grid') {
          // eslint-disable-next-line no-unused-vars
          let exist = false
          switch (element.type) {
            case 'input':
              this.columnList.push({
                code: '',
                len: 50,
                name: element.title
              })
              break
            case 'textarea':
              this.columnList.push({
                code: '',
                len: 50,
                name: element.title
              })
              break
            case 'number':
              this.columnList.push({
                code: '',
                len: 50,
                name: element.title
              })
              break
            case 'radio':
              this.columnList.push({
                code: '',
                len: 50,
                name: element.title
              })
              break
            case 'checkbox':
              this.columnList.push({
                code: '',
                len: 50,
                name: element.title
              })
              break
            case 'datetime':
              this.columnList.push({
                code: '',
                len: 50,
                name: element.title
              })
              break
            case 'select':
              this.columnList.push({
                code: '',
                len: 50,
                name: element.title
              })
              break
            case 'switch':
              this.columnList.push({
                code: '',
                len: 50,
                name: element.title
              })
              break
            default:
              break
          }
        } else {
          element.cols.forEach(element2 => {
            this.packData(element2.list)
          })
        }
      })
    },
    // 保存数据
    save () {
      console.log(this.saveData)
      if (!this.verify()) return
      this.packData(common.deepClone(this.$store.state.formDesign.formList))
      this.saveData.columnList = common.deepClone(this.columnList)
      this.columnList = []
      this.dataListLoading = true
      this.$http({
        url: this.$http.adornUrl('/api-oa/dycform/save'),
        method: 'put',
        data: this.$http.adornData(this.saveData)
      })
        .then(({ data }) => {
          if (data && data.code === 0) {
            this.$message({
              message: '保存成功',
              type: 'success'
            })
            this.dialogVisible = false
          } else {
            this.$message.error(data.msg)
          }
        })
        .catch(() => {})
    },
    init (id) {
      this.visible = true
      this.$nextTick(() => {
        if (common.isEmpty(id)) return
        this.$http({
          url: this.$http.adornUrl('/api-oa/dycform/get'),
          method: 'get',
          params: this.$http.adornParams({ id })
        })
          .then(({ data }) => {
            if (data && data.code === 0) {
              this.saveData = common.deepClone(data.resultData)
              // eslint-disable-next-line no-unused-vars
              let contentStr = data.resultData.content
              let content = JSON.parse(data.resultData.content)
              this.$store.dispatch('formDesign/setFormList', content.formList)
              this.formAttr = content.formAttr
            } else {
              this.$message.error(data.msg)
            }
          })
          .catch(() => {})
      })
    },
    verify () {
      if (common.isEmpty(this.saveData.name)) {
        this.$message({
          message: '表单名称不能为空',
          type: 'warning'
        })
        return false
      }
      if (common.isEmpty(this.saveData.content)) {
        this.$message({
          message: '表单内容不能为空',
          type: 'warning'
        })
        return false
      }
      return true
    },
    // 获取数据列表
    getDataList () {
      this.dataListLoading = true
      this.$http({
        url: this.$http.adornUrl('/api-oa/dyctable/list'),
        method: 'post',
        params: this.$http.adornParams(
          {
            pageNo: this.pageIndex,
            pageSize: this.pageSize,
            name: this.dataForm.name,
            code: this.dataForm.code
          },
          false
        )
      }).then(({ data }) => {
        if (data && data.code === 0) {
          this.dataList = data.resultData.resultList
          this.totalPage = Math.ceil(
            data.resultData.sum / data.resultData.offset
          )
        } else {
          this.dataList = []
          this.totalPage = 0
        }
        this.dataListLoading = false
      })
    }
  },
  mounted () {
    this.saveData = {
      columnList: [],
      content: '',
      name: '',
      remarks: ''
    }
  },
  filters: {
    labelWidth (value) {
      return `${value}px`
    }
  }
}
</script>

